={{ (() => {
  try {
    const allTexts = $json.text;

    // Initialize default values
    let intentData = { intent_score: 2, primary_intent: 'INFORMACION' };
    let businessData = { business_score: 0, business_type: 'NO_MENCIONADO' };
    let urgencyData = { urgency_score: 1, urgency_level: 'NO_EXPRESADO' };

    // Process each text response
    for (let i = 0; i < allTexts.length; i++) {
      try {
        let textToParse = allTexts[i];

        // Clean markdown code blocks - handle both ```json\n and ```json
        textToParse = textToParse.replace(/```json\s*/g, '').replace(/```\s*/g, '').trim();

        // Also handle potential line breaks
        textToParse = textToParse.replace(/^\n+/, '').replace(/\n+$/, '');

        // Try to parse
        const parsed = JSON.parse(textToParse);

        // Categorize by presence of specific fields
        if ('intent_score' in parsed || 'primary_intent' in parsed) {
          intentData = parsed;
        } else if ('business_score' in parsed || 'business_type' in parsed) {
          businessData = parsed;
        } else if ('urgency_score' in parsed || 'urgency_level' in parsed) {
          urgencyData = parsed;
        }
      } catch (e) {
        // Try one more time with aggressive cleaning
        try {
          // Remove all backticks and "json" text
          let cleaned = allTexts[i].replace(/`/g, '').replace(/json/g, '').trim();
          const parsed = JSON.parse(cleaned);

          if ('intent_score' in parsed) intentData = parsed;
          else if ('business_score' in parsed) businessData = parsed;
          else if ('urgency_score' in parsed) urgencyData = parsed;
        } catch (e2) {
          // Skip this text
        }
      }
    }

    // Return extracted scores with proper values
    return {
      intentScore: intentData.intent_score || 2,
      businessScore: businessData.business_score || 0,
      urgencyScore: urgencyData.urgency_score || 1,
      intentType: intentData.primary_intent || 'INFORMACION',
      businessType: businessData.business_type || 'NO_MENCIONADO',
      urgencyLevel: urgencyData.urgency_level || 'NO_EXPRESADO'
    };
  } catch (e) {
    return {
      intentScore: 2,
      businessScore: 0,
      urgencyScore: 1,
      intentType: 'INFORMACION',
      businessType: 'NO_MENCIONADO',
      urgencyLevel: 'NO_EXPRESADO'
    };
  }
})() }}

